import React, { useEffect, useRef, useState } from 'react';
import '../styles/Clock.css';

const SpeechRecognition = window.SpeechRecognition || window.webkitSpeechRecognition;

export default function Clock({ initialSeconds = 25, onExpire } = {}) {
  const [displaySeconds, setDisplaySeconds] = useState(initialSeconds);
  const [running, setRunning] = useState(false);
  const [voiceStatus, setVoiceStatus] = useState('init'); // init | listening | detected | no-support | denied | idle
  const [progress, setProgress] = useState(0);

  // refs for timing & performance
  const durationRef = useRef(initialSeconds * 1000);
  const startTimeRef = useRef(null);
  const rafRef = useRef(null);
  const lastRenderRef = useRef(0); // throttle UI updates
  const audioCtxRef = useRef(null);
  const recognitionRef = useRef(null);
  const shouldListenRef = useRef(true);

  useEffect(() => {
    durationRef.current = initialSeconds * 1000;
    setDisplaySeconds(initialSeconds);
    setProgress(0);
  }, [initialSeconds]);

  // create/reuse AudioContext
  useEffect(() => {
    try {
      audioCtxRef.current = new (window.AudioContext || window.webkitAudioContext)();
    } catch (e) {
      audioCtxRef.current = null;
    }
    return () => {
      if (audioCtxRef.current && audioCtxRef.current.state !== 'closed') {
        try { audioCtxRef.current.close(); } catch (e) {}
      }
    };
  }, []);

  function safePlayBeep(freq = 880, duration = 0.06) {
    const ctx = audioCtxRef.current;
    if (!ctx) return;
    try {
      if (ctx.state === 'suspended') ctx.resume().catch(()=>{});
      const o = ctx.createOscillator();
      const g = ctx.createGain();
      o.type = 'sine';
      o.frequency.value = freq;
      o.connect(g);
      g.connect(ctx.destination);
      g.gain.setValueAtTime(0.001, ctx.currentTime);
      g.gain.exponentialRampToValueAtTime(0.22, ctx.currentTime + 0.01);
      o.start();
      g.gain.exponentialRampToValueAtTime(0.001, ctx.currentTime + duration);
      setTimeout(() => {
        try { o.stop(); } catch (e) {}
      }, duration * 1000 + 50);
    } catch (e) {
      // ignore
    }
  }

  // RAF loop (throttled UI updates)
  function loop(now) {
    if (!startTimeRef.current) startTimeRef.current = now;
    const elapsed = now - startTimeRef.current;
    const t = Math.min(elapsed / durationRef.current, 1);
    const remainingMs = Math.max(durationRef.current - elapsed, 0);
    const remainingS = Math.ceil(remainingMs / 1000);

    // throttle: update UI at ~30fps
    if (now - lastRenderRef.current > 33) {
      setProgress(t);
      setDisplaySeconds(remainingS);
      lastRenderRef.current = now;
    }

    if (t >= 1) {
      // finished
      stopAnimation();
      setRunning(false);
      setProgress(1);
      setDisplaySeconds(0);
      safePlayBeep(440, 0.16);
      onExpire?.();
      return;
    }
    rafRef.current = requestAnimationFrame(loop);
  }

  function stopAnimation() {
    if (rafRef.current) {
      cancelAnimationFrame(rafRef.current);
      rafRef.current = null;
    }
    startTimeRef.current = null;
    lastRenderRef.current = 0;
  }

  function startTimer(secs = null) {
    if (running) return;
    if (secs != null && Number.isFinite(secs) && secs > 0) {
      durationRef.current = secs * 1000;
      setDisplaySeconds(secs);
      setProgress(0);
    }
    setRunning(true);
    safePlayBeep(1200, 0.08);
    stopAnimation();
    rafRef.current = requestAnimationFrame(loop);
  }

  function stopTimer() {
    stopAnimation();
    setRunning(false);
  }

  // Voice recognition setup with safe restart & permission handling
  useEffect(() => {
    if (!SpeechRecognition) {
      setVoiceStatus('no-support');
      shouldListenRef.current = false;
      return;
    }

    const rec = new SpeechRecognition();
    rec.lang = 'en-US';
    rec.interimResults = false;
    rec.maxAlternatives = 1;

    rec.onstart = () => setVoiceStatus('listening');
    rec.onend = () => {
      // only auto-restart if allowed and not denied
      if (shouldListenRef.current && voiceStatus !== 'denied') {
        try { rec.start(); } catch (e) { /* ignore errors */ }
      } else {
        setVoiceStatus('idle');
      }
    };

    rec.onresult = (e) => {
      const phrase = (e.results[0][0].transcript || '').toLowerCase();
      if (/(\bstart\b|\bgo\b|\bbegin\b)/.test(phrase)) {
        setVoiceStatus('detected');
        startTimer();
      } else {
        const numMatch = phrase.match(/\d+/);
        if (numMatch) {
          const n = Number(numMatch[0]);
          if (Number.isFinite(n) && n > 0) startTimer(n);
        }
      }
    };

    rec.onerror = (ev) => {
      if (ev.error === 'not-allowed' || ev.error === 'service-not-allowed') {
        shouldListenRef.current = false;
        setVoiceStatus('denied');
        try { rec.stop(); } catch (e) {}
      } else {
        // keep listening for transient errors
        setVoiceStatus('idle');
      }
    };

    // try to start once
    try {
      rec.start();
      recognitionRef.current = rec;
      shouldListenRef.current = true;
    } catch (e) {
      shouldListenRef.current = false;
      setVoiceStatus('idle');
      recognitionRef.current = null;
    }

    return () => {
      shouldListenRef.current = false;
      try { rec.onresult = null; rec.onend = null; rec.onerror = null; rec.stop(); } catch (e) {}
      recognitionRef.current = null;
    };
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, []); // run once

  const voiceUnavailable = voiceStatus === 'no-support' || voiceStatus === 'denied' || voiceStatus === 'idle';

  // SVG params (kept small to avoid rendering cost on tiny devices)
  const size = 420;
  const stroke = 14;
  const radius = (size - stroke) / 2;
  const circumference = 2 * Math.PI * radius;
  const dashoffset = circumference * (1 - progress);

  return (
    <div className="hq-clock-wrapper">
      <div className="hq-clock-card" role="timer" aria-live="polite" aria-atomic="true">
        <svg className="hq-timer-svg" viewBox={`0 0 ${size} ${size}`} width="100%" height="100%" aria-hidden>
          <defs>
            <filter id="glow" x="-40%" y="-40%" width="200%" height="200%">
              <feGaussianBlur stdDeviation="6" result="coloredBlur"/>
              <feMerge>
                <feMergeNode in="coloredBlur"/>
                <feMergeNode in="SourceGraphic"/>
              </feMerge>
            </filter>
            <linearGradient id="ringGrad" x1="0" x2="1">
              <stop offset="0%" stopColor="#02B3FA" />
              <stop offset="50%" stopColor="#0AEFFF" />
              <stop offset="100%" stopColor="#02B3FA" />
            </linearGradient>
          </defs>

          <circle className="hq-ring-bg" cx={size/2} cy={size/2} r={radius} strokeWidth={stroke} stroke="rgba(2,179,250,0.06)" fill="none" />
          <circle className="hq-ring" cx={size/2} cy={size/2} r={radius} strokeWidth={stroke} stroke="url(#ringGrad)" strokeLinecap="round" fill="none"
            style={{ strokeDasharray: circumference, strokeDashoffset: dashoffset, filter: 'url(#glow)', transition: 'stroke-dashoffset 120ms linear' }}
            transform={`rotate(-90 ${size/2} ${size/2})`} />
          <circle cx={size/2} cy={size/2} r={radius - stroke - 10} fill="rgba(255,255,255,0.02)" stroke="rgba(255,255,255,0.03)" />
        </svg>

        <div className="hq-center">
          <div className="hq-time-value">{displaySeconds}<span className="hq-time-suffix">s</span></div>
          <div className={`hq-voice-status ${voiceStatus}`}>
            {voiceStatus === 'listening' && <span className="dot mic">üéô Listening...</span>}
            {voiceStatus === 'detected' && <span className="dot ok">‚úÖ Voice command detected</span>}
            {voiceStatus === 'no-support' && <span className="dot no">‚ùå Voice not supported</span>}
            {voiceStatus === 'denied' && <span className="dot no">‚ùå Microphone denied</span>}
            {voiceStatus === 'idle' && <span className="dot idle">‚ÑπÔ∏è Voice idle</span>}
          </div>

          {voiceUnavailable && !running && (
            <button className="hq-start-btn" onClick={() => startTimer()} aria-label="Start timer">Start</button>
          )}
        </div>
      </div>
    </div>
  );
}
