import React, { useEffect, useRef, useState } from 'react';
import '../styles/Clock.css';

const SpeechRecognition = window.SpeechRecognition || window.webkitSpeechRecognition;

function playBeep(freq = 1000, duration = 0.06) {
  try {
    const ctx = new (window.AudioContext || window.webkitAudioContext)();
    const o = ctx.createOscillator();
    const g = ctx.createGain();
    o.type = 'sine';
    o.frequency.value = freq;
    o.connect(g);
    g.connect(ctx.destination);
    g.gain.setValueAtTime(0.001, ctx.currentTime);
    g.gain.exponentialRampToValueAtTime(0.2, ctx.currentTime + 0.01);
    o.start();
    g.gain.exponentialRampToValueAtTime(0.001, ctx.currentTime + duration);
    setTimeout(() => { o.stop(); ctx.close(); }, duration * 1000 + 20);
  } catch (e) {
    // ignore audio errors
  }
}

const tryPlayFile = async (src) => {
  try {
    const a = new Audio(src);
    a.volume = 0.9;
    await a.play();
    return true;
  } catch (e) {
    return false;
  }
};

export default function Clock({ initialSeconds = 25, compact = false, onExpire } = {}) {
  const [seconds, setSeconds] = useState(initialSeconds);
  const [running, setRunning] = useState(false);
  const [listening, setListening] = useState(false);
  const [status, setStatus] = useState('');
  const recRef = useRef(null);
  const intervalRef = useRef(null);

  useEffect(() => {
    if (running) {
      intervalRef.current = setInterval(() => {
        setSeconds((s) => {
          if (s <= 1) {
            clearInterval(intervalRef.current);
            setRunning(false);
            (async () => {
              const played = await tryPlayFile('/sounds/end-sound.wav');
              if (!played) playBeep(400, 0.12);
            })();
            onExpire?.();
            setStatus('Finished');
            return 0;
          } else {
            const next = s - 1;
            if (next <= 5) playBeep(1200, 0.04);
            return next;
          }
        });
      }, 1000);
    }
    return () => clearInterval(intervalRef.current);
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, [running]);

  const start = async (secs = null) => {
    if (secs != null) setSeconds(secs);
    setRunning(true);
    setStatus('Running');
    const played = await tryPlayFile('/sounds/start-sound.mp3');
    if (!played) playBeep(1200, 0.08);
  };

  const stop = () => {
    clearInterval(intervalRef.current);
    setRunning(false);
    setStatus('Stopped');
  };

  const reset = (secs = initialSeconds) => {
    clearInterval(intervalRef.current);
    setRunning(false);
    setSeconds(secs);
    setStatus('Reset');
  };

  const startRecognition = () => {
    if (!SpeechRecognition) {
      setStatus('Speech recognition not available');
      return;
    }
    try {
      const rec = new SpeechRecognition();
      rec.lang = 'en-US';
      rec.interimResults = false;
      rec.maxAlternatives = 1;
      rec.onresult = (e) => {
        const phrase = e.results[0][0].transcript.toLowerCase();
        setStatus(`Heard: "${phrase}"`);
        if (phrase.includes('start') || phrase.includes('go') || phrase.includes('begin')) {
          start();
        } else if (phrase.includes('stop') || phrase.includes('pause')) {
          stop();
        } else if (phrase.includes('reset') || phrase.includes('again')) {
          reset();
        } else if (phrase.match(/\d+/)) {
          // If user says a number, start with that many seconds
          const n = Number((phrase.match(/\d+/) || [])[0]);
          if (!Number.isNaN(n) && n > 0) start(n);
        }
      };
      rec.onend = () => {
        if (listening) {
          try { rec.start(); } catch (e) { }
        }
      };
      rec.onerror = (ev) => {
        console.warn('Speech error', ev);
        setStatus('Voice error');
      };
      rec.start();
      recRef.current = rec;
      setListening(true);
      setStatus('Listening for "start", "stop", "reset" or a number');
    } catch (e) {
      setStatus('Could not start speech recognition');
    }
  };

  const stopRecognition = () => {
    try { recRef.current?.stop(); } catch (e) { }
    recRef.current = null;
    setListening(false);
    setStatus('Voice stopped');
  };

  const containerClass = compact ? 'countdown-container compact' : 'countdown-container';

  return (
    <div className={containerClass} aria-live="polite">
      <div className="outer-ring">
        <div className="inner-ring">
          <span id="time">{String(seconds).padStart(2, '0')}</span>
        </div>
      </div>

      <div className={`clock-controls ${compact ? 'compact-controls' : ''}`}>
        {!running ? (
          <button className="btn" onClick={() => start()} aria-label="Start timer">Start</button>
        ) : (
          <button className="btn" onClick={stop} aria-label="Stop timer">Stop</button>
        )}
        <button className="btn" onClick={() => reset()} aria-label="Reset timer">Reset</button>
        <button
          className={`btn ${listening ? 'active' : ''}`}
          onClick={() => (listening ? stopRecognition() : startRecognition())}
          aria-pressed={listening}
        >
          {listening ? 'Stop Voice' : 'Voice Start'}
        </button>
      </div>

      <div className="clock-status">
        <small>{status}</small>
      </div>
    </div>
  );
}
