import React, { useEffect, useRef, useState } from 'react';
import '../styles/Clock.css';

const SpeechRecognition = window.SpeechRecognition || window.webkitSpeechRecognition;

/*
 Minimal, premium game-show countdown clock
 - Fullscreen, centered, single circular element
 - Voice start (start/go/begin). Invisible; no extra text.
 - One fallback "Start" button shown ONLY if voice not supported/denied
 - Smooth RAF-based animation, tick every second, buzzer at end
 - Careful cleanup for RAF and SpeechRecognition
*/

export default function Clock({ initialSeconds = 25, onExpire } = {}) {
  const [secondsLeft, setSecondsLeft] = useState(initialSeconds);
  const [progress, setProgress] = useState(0); // 0..1
  const [running, setRunning] = useState(false);
  const [showFallback, setShowFallback] = useState(false); // show Start button
  const audioCtxRef = useRef(null);
  const recRef = useRef(null);
  const shouldListenRef = useRef(true);
  const durationRef = useRef(initialSeconds * 1000);
  const startTimeRef = useRef(null);
  const rafRef = useRef(null);
  const lastSecondRef = useRef(null);

  useEffect(() => {
    durationRef.current = initialSeconds * 1000;
    setSecondsLeft(initialSeconds);
    setProgress(0);
  }, [initialSeconds]);

  useEffect(() => {
    try { audioCtxRef.current = new (window.AudioContext || window.webkitAudioContext)(); }
    catch (e) { audioCtxRef.current = null; }
    return () => {
      // cleanup audio context
      try { if (audioCtxRef.current && audioCtxRef.current.state !== 'closed') audioCtxRef.current.close(); } catch (e) {}
    };
  }, []);

  // small tick sound for each second
  function tickSound() {
    const ctx = audioCtxRef.current;
    if (!ctx) return;
    try {
      if (ctx.state === 'suspended') ctx.resume().catch(()=>{});
      const o = ctx.createOscillator();
      const g = ctx.createGain();
      o.type = 'square';
      o.frequency.value = 1200;
      o.connect(g);
      g.connect(ctx.destination);
      g.gain.setValueAtTime(0.001, ctx.currentTime);
      g.gain.exponentialRampToValueAtTime(0.15, ctx.currentTime + 0.005);
      o.start();
      g.gain.exponentialRampToValueAtTime(0.001, ctx.currentTime + 0.06);
      setTimeout(()=>{ try { o.stop(); } catch(e){} }, 90);
    } catch (e) {}
  }

  // buzzer / "buww" at end
  function buzzer() {
    const ctx = audioCtxRef.current;
    if (!ctx) return;
    try {
      if (ctx.state === 'suspended') ctx.resume().catch(()=>{});
      const o = ctx.createOscillator();
      const g = ctx.createGain();
      o.type = 'sawtooth';
      o.frequency.value = 120; // low tone
      o.connect(g);
      g.connect(ctx.destination);
      g.gain.setValueAtTime(0.001, ctx.currentTime);
      g.gain.linearRampToValueAtTime(0.25, ctx.currentTime + 0.02);
      o.start();
      // glide down for a short "buww"
      o.frequency.exponentialRampToValueAtTime(60, ctx.currentTime + 0.45);
      g.gain.exponentialRampToValueAtTime(0.001, ctx.currentTime + 0.6);
      setTimeout(()=>{ try { o.stop(); } catch(e){} }, 700);
    } catch (e) {}
  }

  // RAF animation loop
  function rafLoop(now) {
    if (!startTimeRef.current) startTimeRef.current = now;
    const elapsed = now - startTimeRef.current;
    const t = Math.min(elapsed / durationRef.current, 1);
    const remainingMs = Math.max(durationRef.current - elapsed, 0);
    const remainingS = Math.ceil(remainingMs / 1000);

    // update displayed values (throttle naturally to RAF)
    setProgress(t);
    setSecondsLeft(remainingS);

    // tick on second change
    if (lastSecondRef.current == null) lastSecondRef.current = remainingS;
    if (remainingS !== lastSecondRef.current && remainingS >= 0) {
      lastSecondRef.current = remainingS;
      if (remainingS > 0) tickSound();
    }

    if (t >= 1) {
      // end
      cancelAnimationFrame(rafRef.current);
      rafRef.current = null;
      startTimeRef.current = null;
      lastSecondRef.current = null;
      setProgress(1);
      setSecondsLeft(0);
      setRunning(false);
      buzzer();
      onExpire?.();
      return;
    }
    rafRef.current = requestAnimationFrame(rafLoop);
  }

  function startTimer(secs = null) {
    if (running) return;
    if (secs != null && Number.isFinite(secs) && secs > 0) {
      durationRef.current = secs * 1000;
      setSecondsLeft(secs);
    } else {
      durationRef.current = initialSeconds * 1000;
      setSecondsLeft(Math.ceil(durationRef.current / 1000));
    }
    setRunning(true);
    // start RAF
    stopRAF();
    // quick start tick
    tickSound();
    rafRef.current = requestAnimationFrame(rafLoop);
    // after voice-trigger, stop listening to avoid double triggers
    if (recRef.current) {
      try { shouldListenRef.current = false; recRef.current.stop(); } catch (e) {}
    }
  }

  function stopRAF() {
    if (rafRef.current) {
      cancelAnimationFrame(rafRef.current);
      rafRef.current = null;
    }
    startTimeRef.current = null;
    lastSecondRef.current = null;
  }

  // Voice recognition: start once and listen until success or denied
  useEffect(() => {
    if (!SpeechRecognition) {
      setShowFallback(true);
      return;
    }
    let rec;
    try {
      rec = new SpeechRecognition();
      rec.lang = 'en-US';
      rec.interimResults = false;
      rec.maxAlternatives = 1;
    } catch (e) {
      setShowFallback(true);
      return;
    }

    rec.onresult = (e) => {
      const phrase = (e.results[0][0].transcript || '').toLowerCase();
      if (/(\bstart\b|\bgo\b|\bbegin\b)/.test(phrase)) {
        startTimer();
        setShowFallback(false);
      } else {
        // also accept a spoken number
        const m = phrase.match(/\d+/);
        if (m) {
          const val = Number(m[0]);
          if (Number.isFinite(val) && val > 0) startTimer(val);
        }
      }
    };

    rec.onerror = (ev) => {
      if (ev.error === 'not-allowed' || ev.error === 'service-not-allowed') {
        setShowFallback(true);
        shouldListenRef.current = false;
        try { rec.stop(); } catch (e) {}
      }
    };

    rec.onend = () => {
      // keep listening only if allowed and we haven't started
      if (shouldListenRef.current && !running) {
        try { rec.start(); } catch (e) { setShowFallback(true); }
      }
    };

    try {
      rec.start();
      recRef.current = rec;
      shouldListenRef.current = true;
    } catch (e) {
      setShowFallback(true);
    }

    return () => {
      shouldListenRef.current = false;
      try { rec.onresult = null; rec.onerror = null; rec.onend = null; rec.stop(); } catch (e) {}
      recRef.current = null;
    };
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, []); // run once

  // SVG ring parameters (for visual ring)
  const size = 520;
  const stroke = 14;
  const radius = (size - stroke) / 2;
  const circumference = 2 * Math.PI * radius;
  const dashoffset = circumference * progress;

  return (
    <div className="minimal-clock-wrapper">
      <div className="minimal-clock">
        <svg className="minimal-ring" viewBox={`0 0 ${size} ${size}`} width="100%" height="100%" aria-hidden>
          <defs>
            <filter id="soft-glow" x="-50%" y="-50%" width="200%" height="200%">
              <feGaussianBlur stdDeviation="10" result="blur"/>
              <feMerge><feMergeNode in="blur"/><feMergeNode in="SourceGraphic"/></feMerge>
            </filter>
          </defs>

          {/* outer glowing ring background */}
          <circle cx={size/2} cy={size/2} r={radius} strokeWidth={stroke} stroke="rgba(10,24,39,0.1)" fill="none" />
          {/* animated progress ring */}
          <circle cx={size/2} cy={size/2} r={radius} strokeWidth={stroke} stroke="url(#ringGrad)" strokeLinecap="round" fill="none"
            style={{ strokeDasharray: circumference, strokeDashoffset: dashoffset, filter: 'url(#soft-glow)', transition: 'stroke-dashoffset 120ms linear' }}
            transform={`rotate(-90 ${size/2} ${size/2})`} />
          {/* inner circle for contrast */}
          <circle cx={size/2} cy={size/2} r={radius - stroke - 10} fill="rgba(255,255,255,0.02)" stroke="rgba(255,255,255,0.03)" />
        </svg>

        <div className="minimal-center">
          <div className="minimal-time-value">{secondsLeft}<span className="minimal-time-suffix">s</span></div>
          {showFallback && !running && (
            <button className="minimal-start-btn" onClick={() => startTimer()} aria-label="Start timer">Start</button>
          )}
        </div>
      </div>
    </div>
  );
}
