import React, { useEffect, useRef, useState } from 'react';
import '../styles/Clock.css';

/*
  Game-show styled Countdown Clock (React)
  - Circular SVG progress ring with smooth animation (requestAnimationFrame)
  - Neon cyan glow, dark navy background
  - Voice control via Web Speech API (start/go/begin)
  - Single fallback "Start" button when voice not available/denied
  - Configurable duration via `initialSeconds` prop (default 25)
  - Clean, commented and responsive
*/

const SpeechRecognition = window.SpeechRecognition || window.webkitSpeechRecognition;

function playBeep(freq = 880, duration = 0.08) {
  try {
    const ctx = new (window.AudioContext || window.webkitAudioContext)();
    const o = ctx.createOscillator();
    const g = ctx.createGain();
    o.type = 'sine';
    o.frequency.value = freq;
    o.connect(g);
    g.connect(ctx.destination);
    g.gain.setValueAtTime(0.001, ctx.currentTime);
    g.gain.exponentialRampToValueAtTime(0.25, ctx.currentTime + 0.01);
    o.start();
    g.gain.exponentialRampToValueAtTime(0.001, ctx.currentTime + duration);
    setTimeout(() => { o.stop(); ctx.close(); }, duration * 1000 + 20);
  } catch (e) {
    // audio not available / blocked
  }
}

export default function Clock({ initialSeconds = 25, onExpire } = {}) {
  // Visible state (for rendering)
  const [displaySeconds, setDisplaySeconds] = useState(initialSeconds);
  const [running, setRunning] = useState(false);
  const [voiceStatus, setVoiceStatus] = useState('init'); // init | listening | detected | no-support | denied | idle
  const [progress, setProgress] = useState(0); // 0..1 for ring
  const rafRef = useRef(null);

  // Timer internal refs to avoid unnecessary re-renders
  const startRef = useRef(null);
  const durationRef = useRef(initialSeconds * 1000);
  const pausedRef = useRef(false);
  const recognitionRef = useRef(null);

  // Update duration when prop changes
  useEffect(() => {
    durationRef.current = initialSeconds * 1000;
    setDisplaySeconds(initialSeconds);
    setProgress(0);
  }, [initialSeconds]);

  // --- RequestAnimationFrame loop for smooth progress ---
  function animate(now) {
    if (!startRef.current) startRef.current = now;
    const elapsed = now - startRef.current;
    const t = Math.min(elapsed / durationRef.current, 1);
    setProgress(t);
    const remainingMs = Math.max(durationRef.current - elapsed, 0);
    setDisplaySeconds(Math.ceil(remainingMs / 1000));
    if (t < 1 && !pausedRef.current) {
      rafRef.current = requestAnimationFrame(animate);
    } else {
      // finished
      stopAnimation();
      setRunning(false);
      setProgress(1);
      setDisplaySeconds(0);
      playBeep(440, 0.18); // final sound
      onExpire?.();
    }
  }

  function stopAnimation() {
    if (rafRef.current) {
      cancelAnimationFrame(rafRef.current);
      rafRef.current = null;
    }
    startRef.current = null;
  }

  function startTimer(durationSeconds = null) {
    if (running) return; // ignore repeated starts
    if (durationSeconds !== null) {
      durationRef.current = durationSeconds * 1000;
      setDisplaySeconds(durationSeconds);
    }
    setRunning(true);
    pausedRef.current = false;
    setVoiceStatus('detected');
    // small start sound
    playBeep(1200, 0.08);
    // start RAF
    stopAnimation();
    rafRef.current = requestAnimationFrame(animate);
  }

  function stopTimer() {
    stopAnimation();
    setRunning(false);
    pausedRef.current = true;
  }

  // --- Voice recognition setup ---
  useEffect(() => {
    if (!SpeechRecognition) {
      setVoiceStatus('no-support');
      return;
    }
    try {
      const rec = new SpeechRecognition();
      rec.lang = 'en-US';
      rec.interimResults = false;
      rec.maxAlternatives = 1;

      rec.onstart = () => setVoiceStatus('listening');
      rec.onend = () => {
        // auto-restart listening unless user/permission denied
        if (voiceStatus !== 'denied' && !running) {
          try { rec.start(); } catch (e) { /* ignore */ }
        } else if (!running) {
          setVoiceStatus('idle');
        }
      };

      rec.onresult = (e) => {
        const phrase = (e.results[0][0].transcript || '').toLowerCase();
        // recognized keywords
        if (/(\bstart\b|\bgo\b|\bbegin\b)/.test(phrase)) {
          setVoiceStatus('detected');
          startTimer();
        } else {
          // allow "30" or "thirty" numeric detection (basic)
          const numMatch = phrase.match(/\d+/);
          if (numMatch) {
            const n = Number(numMatch[0]);
            if (!Number.isNaN(n) && n > 0) {
              startTimer(n);
            }
          }
        }
      };

      rec.onerror = (ev) => {
        // permission denied or other errors
        if (ev.error === 'not-allowed' || ev.error === 'service-not-allowed') {
          setVoiceStatus('denied');
          try { rec.stop(); } catch (e) {}
        } else {
          // transient errors, try to keep listening
          setVoiceStatus('idle');
        }
      };

      // start listening immediately (so user can speak "start")
      try {
        rec.start();
        recognitionRef.current = rec;
      } catch (e) {
        setVoiceStatus('idle');
        recognitionRef.current = null;
      }
      // cleanup
      return () => {
        try { rec.onresult = null; rec.onend = null; rec.onerror = null; rec.stop(); } catch (e) {}
        recognitionRef.current = null;
      };
    } catch (e) {
      setVoiceStatus('no-support');
      recognitionRef.current = null;
    }
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, []); // run once

  // If voice is denied, show fallback Start button. If voice works, keep minimal UI.
  const voiceUnavailable = voiceStatus === 'no-support' || voiceStatus === 'denied' || voiceStatus === 'idle';

  // SVG ring parameters
  const size = 420; // base size (responsive via CSS)
  const stroke = 14;
  const radius = (size - stroke) / 2;
  const circumference = 2 * Math.PI * radius;
  const dashoffset = circumference * (1 - progress);

  return (
    <div className="hq-clock-wrapper">
      <div className="hq-clock-card" role="timer" aria-live="polite" aria-atomic="true">
        <svg className="hq-timer-svg" viewBox={`0 0 ${size} ${size}`} width="100%" height="100%" aria-hidden>
          <defs>
            <filter id="glow" x="-40%" y="-40%" width="200%" height="200%">
              <feGaussianBlur stdDeviation="8" result="coloredBlur"/>
              <feMerge>
                <feMergeNode in="coloredBlur"/>
                <feMergeNode in="SourceGraphic"/>
              </feMerge>
            </filter>
            <linearGradient id="ringGrad" x1="0" x2="1">
              <stop offset="0%" stopColor="#02B3FA" />
              <stop offset="50%" stopColor="#0AEFFF" />
              <stop offset="100%" stopColor="#02B3FA" />
            </linearGradient>
          </defs>

          {/* background ring (subtle) */}
          <circle
            className="hq-ring-bg"
            cx={size/2}
            cy={size/2}
            r={radius}
            strokeWidth={stroke}
            stroke="rgba(2,179,250,0.06)"
            fill="none"
          />

          {/* animated ring */}
          <circle
            className="hq-ring"
            cx={size/2}
            cy={size/2}
            r={radius}
            strokeWidth={stroke}
            stroke="url(#ringGrad)"
            strokeLinecap="round"
            fill="none"
            style={{
              strokeDasharray: circumference,
              strokeDashoffset: dashoffset,
              filter: 'url(#glow)',
              transition: 'stroke-dashoffset 120ms linear'
            }}
            transform={`rotate(-90 ${size/2} ${size/2})`}
          />

          {/* inner glass disc */}
          <circle
            cx={size/2}
            cy={size/2}
            r={radius - stroke - 10}
            fill="rgba(255,255,255,0.02)"
            stroke="rgba(255,255,255,0.03)"
          />
        </svg>

        {/* Centered time display */}
        <div className="hq-center">
          <div className="hq-time-value">{displaySeconds}<span className="hq-time-suffix">s</span></div>
          <div className={`hq-voice-status ${voiceStatus}`}>
            {/* status icons */}
            {voiceStatus === 'listening' && <span className="dot mic">üéô Listening...</span>}
            {voiceStatus === 'detected' && <span className="dot ok">‚úÖ Voice command detected</span>}
            {voiceStatus === 'no-support' && <span className="dot no">‚ùå Voice not supported</span>}
            {voiceStatus === 'denied' && <span className="dot no">‚ùå Microphone denied</span>}
            {voiceStatus === 'idle' && <span className="dot idle">‚ÑπÔ∏è Voice idle</span>}
          </div>

          {/* Fallback Start button (only visible when voice unavailable) */}
          {voiceUnavailable && !running && (
            <button
              className="hq-start-btn"
              onClick={() => startTimer()}
              aria-label="Start timer"
            >
              Start
            </button>
          )}
        </div>
      </div>
    </div>
  );
}
